import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

function login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });

      if (response.data.message === 'Login bem-sucedido!') {
        navigate('/dashboard');
      } else {
        setError('Credenciais inválidas');
      }
    } catch (error) {
      setError('Erro ao conectar ao servidor');
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', background: '#111' }}>
      <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '20px', background: '#222', padding: '40px', borderRadius: '10px' }}>
        <h2 style={{ color: '#fff' }}>Login</h2>
        <input
          type="text"
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ padding: '10px', borderRadius: '5px', width: '200px' }}
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: '10px', borderRadius: '5px', width: '200px' }}
        />
        <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', background: '#0078ff', color: '#fff' }}>
          Entrar
        </button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <Link to="/register" style={{ color: '#fff', marginTop: '10px' }}>Não tem uma conta? Cadastre-se</Link>
      </form>
    </div>
  );
}

export default login;
