import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function registrar() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/register', {
        username,
        password
      });

      if (response.data.message === 'Cadastro bem-sucedido!') {
        setSuccess('Usuário registrado com sucesso! Redirecionando para login...');
        setTimeout(() => {
          navigate('/');
        }, 2000); // Redireciona para a página de login após 2 segundos
      } else {
        setError(response.data.message || 'Erro ao registrar usuário');
      }
    } catch (error) {
      setError('Erro ao conectar ao servidor');
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', background: '#111' }}>
      <form onSubmit={handleRegister} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '20px', background: '#222', padding: '40px', borderRadius: '10px' }}>
        <h2 style={{ color: '#fff' }}>Cadastro</h2>
        <input
          type="text"
          placeholder="Usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ padding: '10px', borderRadius: '5px', width: '200px' }}
        />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: '10px', borderRadius: '5px', width: '200px' }}
        />
        <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', background: '#0078ff', color: '#fff' }}>
          Registrar
        </button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}
      </form>
    </div>
  );
}

export default registrar;
